mapper.update:
UPDATE MEMBER SET

	MEMBER_IMG1 = #{member_img1}


	MEMBER_IMG2 = #{member_img2}


	MEMBER_IMG3 = #{member_img3}

WHERE MEMBER_ID = #{member_id}

UPDATE MEMBER SET
MEMBER_INTRODUCTION = #{member_introduction}
WHERE MEMBER_ID = #{member_id}

mapper.select:
SELECT MEMBER_ID ,MEMBER_PASSWORD ,MEMBER_NICKNAME ,MEMBER_SEX ,MEMBER_COUNTRY ,ROUND(MONTHS_BETWEEN(SYSDATE,MEMBER_BIRTH)/12) + 1 AS "MEMBER_BIRTH" ,MEMBER_INTRODUCTION ,MEMBER_LEVEL ,MEMBER_PROFILE_IMG ,MEMBER_INTEREST1 ,MEMBER_INTEREST2 ,MEMBER_INTEREST3 ,MEMBER_NAME ,MEMBER_WARNING ,(CASE WHEN MEMBER_INTEREST1 IN (#{interest1}, #{interest2}, #{interest3}) THEN 1 else 0 END + CASE WHEN MEMBER_INTEREST2 IN (#{interest1}, #{interest2}, #{interest3}) THEN 1 else 0 END + CASE WHEN MEMBER_INTEREST3 IN (#{interest1}, #{interest2}, #{interest3}) THEN 1 else 0 END) AS "COUNT" FROM MEMBER WHERE MEMBER_COUNTRY = #{optionsRadios}
AND MEMBER_SEX != #{vo.member_sex}
AND MEMBER_SEX = #{vo.member_sex} AND ROUND(MONTHS_BETWEEN(SYSDATE,MEMBER_BIRTH)/12) + 1 BETWEEN #{minage} AND #{maxage} AND MEMBER_LEVEL = '2'
AND (MEMBER_INTEREST1 IN (#{interest1}, #{interest2}, #{interest3}) OR MEMBER_INTEREST2 IN (#{interest1}, #{interest2}, #{interest3}) OR MEMBER_INTEREST3 IN (#{interest1}, #{interest2}, #{interest3}))
ORDER BY COUNT DESC
